    "import pandas as pd\n",
    "import torch\n",
    "from transformers import BertTokenizer, BertForSequenceClassification, Trainer, TrainingArguments, pipeline\n",
    "from sklearn.model_selection import train_test_split\n",
    "from torch.utils.data import Dataset\n",
    "from sklearn.feature_extraction.text import CountVectorizer\n",
    "from sklearn.decomposition import LatentDirichletAllocation\n",
    "\n",
    "# **Step 1: Coreference Resolution Function**\n",
    "def resolve_coreferences(text):\n",
    "    \"\"\"\n",
    "    Perform simple rule-based coreference resolution.\n",
    "    \"\"\"\n",
    "    sentences = text.split(\". \")\n",
    "    entities = {}\n",
    "    resolved_text = []\n",
    "    for sentence in sentences:\n",
    "        words = sentence.split()\n",
    "        for i, word in enumerate(words):\n",
    "            if word.istitle() and word not in entities:\n",
    "                entities[word] = word\n",
    "            elif word.lower() in [\"he\", \"she\", \"him\", \"her\", \"his\", \"hers\"]:\n",
    "                if len(entities) > 0:\n",
    "                    words[i] = list(entities.keys())[-1]\n",
    "        resolved_text.append(\" \".join(words))\n",
    "    return \". \".join(resolved_text)\n",
    "\n",
    "# **Step 2: Load IMDB Dataset**\n",
    "file_path = \"IMDB_Dataset.csv\"  # Update with the actual path\n",
    "data = pd.read_csv(file_path, nrows=1000)\n",
    "data.dropna(inplace=True)\n",
    "\n",
    "# Rename columns and convert labels\n",
    "data.rename(columns={\"review\": \"text\", \"sentiment\": \"label\"}, inplace=True)\n",
    "data[\"label\"] = data[\"label\"].map({\"positive\": 1, \"negative\": 0})\n",
    "\n",
    "# Resolve coreferences\n",
    "data[\"resolved_text\"] = data[\"text\"].apply(resolve_coreferences)\n",
    "\n",
    "# **Step 3: Split Data**\n",
    "train_texts, test_texts, train_labels, test_labels = train_test_split(\n",
    "    data[\"text\"], data[\"label\"], test_size=0.2, random_state=42, stratify=data[\"label\"]\n",
    ")\n",
    "train_resolved, test_resolved = train_test_split(\n",
    "    data[\"resolved_text\"], test_size=0.2, random_state=42, stratify=data[\"label\"]\n",
    ")\n",
    "\n"
